package com.niit.mr1;

public class NaturalNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		for(i=10; i>0;i--)
		{
			System.out.println(i);
		}
		i=10;
		while(i>0){
			System.out.println(i);
			i--;
		}
		i=10;
		do{
			System.out.println(i);
			i--;
		}while(i>0);
	}

}
